INSERT INTO public.site_settings (id, config) VALUES (1, '{"home": {"heroImage": "https://drive.google.com/file/d/1Qo6kjfz74kZpWp6a--9Dyi8H8I0DvnVH/view?usp=drivesdk", "scheduleUrl": "https://drive.google.com/file/d/1n2_APLOMYrPY5yVDKWY2RSNEL10AaRvH/view?usp=drivesdk", "vision": "الريادة في تقديم تعليم رياضي يصنع جيلاً يمتلك أدوات التحليل المنطقي، قادراً على المنافسة بقوة وتفوق في المقاييس الوطنية والدولية، ومساهماً في بناء مستقبل وطنه.", "mission": "تحويل الرياضيات من مجرد أرقام مجردة إلى مهارات تفكير حية، عبر بيئة تعليمية ملهمة، تصنع من طلاب اليوم قادة قادرين على التحليل، الابتكار، وصناعة المستقبل.", "objectives": [{"title": "الجانب المعرفي والمهاري", "icon": "fa-brain", "items": ["تبسيط المفاهيم الرياضية المجردة وربطها بمواقف الحياة اليومية الملموسة.", "تنمية مهارات التفكير الناقد، والتحليل المنطقي، وبناء استراتيجيات ذكية لحل المشكلات.", "رفع مؤشرات الأداء والتحصيل العلمي في الاختبارات المركزية والوطنية (نافس) والدولية (TIMSS)."]}, {"title": "الجانب النفسي والتحفيزي", "icon": "fa-face-smile-beam", "items": ["كسر حاجز الرهبة من مادة الرياضيات، وبناء ثقة المتعلم في قدراته العقلية والتحليلية.", "رعاية الفروق الفردية من خلال خطط إثرائية تتحدى قدرات الموهوبين، وخطط علاجية فعالة لسد الفاقد التعليمي."]}, {"title": "الجانب التقني والابتكاري", "icon": "fa-lightbulb", "items": ["التوظيف الفعال للتقنية الحديثة ومعامل الرياضيات الافتراضية (Interactive Labs) لتحويل التعلم من التلقين إلى الاستكشاف.", "التنويع المستمر في استراتيجيات التدريس بما يتناسب مع أنماط التعلم المختلفة للطلاب."]}, {"title": "الجانب المهني والمجتمعي", "icon": "fa-handshake", "items": ["تفعيل الشراكة الإيجابية والمثمرة مع أولياء الأمور لدعم مسيرة الطالب التعليمية.", "الإسهام الفعال في مجتمعات التعلم المهنية، وتبادل الخبرات مع الزملاء للارتقاء ببيئة العمل.", "غرس القيم الأخلاقية والوطنية في نفوس الطلاب بما ينسجم مع أهداف سياسة التعليم ورؤية المملكة."]}], "stats": [{"label": "سنوات الخبرة", "value": "15+"}, {"label": "الدورات المنجزة", "value": "45"}, {"label": "نسبة النجاح", "value": "98%"}, {"label": "مباد